class Solution:
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        t_stack = []
        maxlen = 0
        t_stack.append(-1)
        for  iter in  range(0, len(s)):
            if s[iter] == '(':
                t_stack.append(iter)
            else:
                t_stack.pop()
                if  len(t_stack) < 1:
                    t_stack.append(iter)
                else:
                    maxlen= max(maxlen,iter-t_stack[-1])
        return maxlen
      


